/****** Object:  View [dbo].[ASSETS_ATTRIBUTES]    Committed by VersionSQL https://www.versionsql.com ******/

CREATE VIEW ASSETS_ATTRIBUTES AS SELECT ASSETS.* , A1.VALUE [Attribute.INVOICE NUMBER], A2.VALUE [Attribute.Located], A3.VALUE [Attribute.SUPPLIER NAME] FROM ASSETS  LEFT OUTER JOIN ATTRIBVALUE A1 on A1.TABLENAME = 'ASSETS' and A1.ATTRIBUTE = 'INVOICE NUMBER' and A1.COLKEY = ASSETS.ASSETID and A1.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A2 on A2.TABLENAME = 'ASSETS' and A2.ATTRIBUTE = 'Located' and A2.COLKEY = ASSETS.ASSETID and A2.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A3 on A3.TABLENAME = 'ASSETS' and A3.ATTRIBUTE = 'SUPPLIER NAME' and A3.COLKEY = ASSETS.ASSETID and A3.ARRAYINDEX = 0