CREATE VIEW dbo.CIRBFPRO  AS  SELECT     dbo.EMPLOYEES.EMPNUMBER, dbo.EMPLOYEES.PAYROLLID, dbo.EMPLOYEES.TERMDATE, dbo.EMPLOYEES.SEX,   dbo.EMPLOYEES.MARITALSTATUS, dbo.EMPLOYEES.BIRTHDAY, dbo.EMPLOYEES.TAXNUMBER, dbo.EMPLOYEES.IDNUM, dbo.EMPLOYEES.RATE1,   dbo.EMPLOYEES.PENDATE, dbo.EMPLOYEES.INITIALS, dbo.EMPLOYEES.SURNAME, dbo.EMPLOYEES.GRSTARTDATE, dbo.EMPLOYEES.GRADE,  SUM(dbo.EMPED.THEMONEY) AS PRO, dbo.EMPEDSETT.USEIT, dbo.EMPED.PERIODNO, dbo.EMPED.YEARNO, dbo.EMPED.RUNNO  FROM         dbo.EMPEDSETT INNER JOIN  dbo.EMPED INNER JOIN  dbo.EMPLOYEES ON dbo.EMPED.PRLID = dbo.EMPLOYEES.PAYROLLID AND dbo.EMPED.EMPNUMBER = dbo.EMPLOYEES.EMPNUMBER ON   dbo.EMPEDSETT.PRLID = dbo.EMPED.PRLID AND dbo.EMPEDSETT.EMPNUMBER = dbo.EMPED.EMPNUMBER AND  dbo.EMPEDSETT.EDSNUMBER = dbo.EMPED.EDSNUMBER AND dbo.EMPEDSETT.EDSCODE = dbo.EMPED.EDSCODE AND   dbo.EMPEDSETT.EDSHID = dbo.EMPED.EDSHID  GROUP BY dbo.EMPLOYEES.GRADE, dbo.EMPLOYEES.PAYROLLID, dbo.EMPLOYEES.TERMDATE, dbo.EMPLOYEES.SEX, dbo.EMPLOYEES.MARITALSTATUS,   dbo.EMPLOYEES.BIRTHDAY, dbo.EMPLOYEES.TAXNUMBER, dbo.EMPLOYEES.IDNUM, dbo.EMPLOYEES.RATE1, dbo.EMPLOYEES.GRSTARTDATE,  dbo.EMPLOYEES.SURNAME, dbo.EMPLOYEES.INITIALS, dbo.EMPLOYEES.PENDATE, dbo.EMPEDSETT.USEIT, dbo.EMPEDSETT.EDSCODE,  dbo.EMPLOYEES.EMPNUMBER, dbo.EMPED.PERIODNO, dbo.EMPED.YEARNO, dbo.EMPED.RUNNO  HAVING      (dbo.EMPEDSETT.EDSCODE = N'PRO') AND (dbo.EMPEDSETT.USEIT = 1) 