CREATE VIEW dbo.CIRBFTOT  AS  SELECT SUM(Other) AS tot, EMPNUMBER, PAYROLLID, TERMDATE, SEX, MARITALSTATUS, BIRTHDAY, TAXNUMBER, IDNUM, RATE1, PENDATE, INITIALS,  SURNAME, GRSTARTDATE, GRADE, PERIODNO, RUNNO, YEARNO  FROM dbo.CIRBFOther  GROUP BY EMPNUMBER, PAYROLLID, TERMDATE, SEX, MARITALSTATUS, BIRTHDAY, TAXNUMBER, IDNUM, RATE1, PENDATE, INITIALS, SURNAME,  GRSTARTDATE, GRADE, PERIODNO, RUNNO, YEARNO  HAVING (SUM(Other) > 0) 