CREATE VIEW CREDITORS_ATTRIBUTES AS SELECT CREDITORS.* , A13.VALUE [Attribute.Beneficiary Name], A14.VALUE [Attribute.GST] FROM CREDITORS  LEFT OUTER JOIN ATTRIBVALUE A13 on A13.TABLENAME = 'CREDITORS' and A13.ATTRIBUTE = 'Beneficiary Name' and A13.COLKEY = CREDITORS.CREDNUMBER and A13.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A14 on A14.TABLENAME = 'CREDITORS' and A14.ATTRIBUTE = 'GST' and A14.COLKEY = CREDITORS.CREDNUMBER and A14.ARRAYINDEX = 0