/****** Object:  View [dbo].[DEBTORS_ATTRIBUTES]    Committed by VersionSQL https://www.versionsql.com ******/

CREATE VIEW DEBTORS_ATTRIBUTES AS SELECT DEBTORS.* , A15.VALUE [Attribute.1-Project], A16.VALUE [Attribute.2-Block], A17.VALUE [Attribute.3-Floor], A18.VALUE [Attribute.4-Flat], A19.VALUE [Attribute.GST] FROM DEBTORS  LEFT OUTER JOIN ATTRIBVALUE A15 on A15.TABLENAME = 'DEBTORS' and A15.ATTRIBUTE = '1-Project' and A15.COLKEY = DEBTORS.DEBTNUMBER and A15.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A16 on A16.TABLENAME = 'DEBTORS' and A16.ATTRIBUTE = '2-Block' and A16.COLKEY = DEBTORS.DEBTNUMBER and A16.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A17 on A17.TABLENAME = 'DEBTORS' and A17.ATTRIBUTE = '3-Floor' and A17.COLKEY = DEBTORS.DEBTNUMBER and A17.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A18 on A18.TABLENAME = 'DEBTORS' and A18.ATTRIBUTE = '4-Flat' and A18.COLKEY = DEBTORS.DEBTNUMBER and A18.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A19 on A19.TABLENAME = 'DEBTORS' and A19.ATTRIBUTE = 'GST' and A19.COLKEY = DEBTORS.DEBTNUMBER and A19.ARRAYINDEX = 0