  CREATE VIEW INVSTORES_VIEW  AS  	SELECT INVSTORES.StoreCode, INVSTORES.StoreName, temp.BorgID, isnull(INVSTORESCOSDIV.DivId, -1) as DiviD, ISNULL(INVSTORESCOSDIV.HireDivId, - 1) AS HireDiviD,       ISNULL(INVSTORESCOSDIV.StoreContNumber, '') AS StoreContNumber, ISNULL(INVSTORESCOSDIV.StoreDefContGL, '') AS StoreDefContGL,       ISNULL(INVSTORESCOSDIV.StoreContWriteOffGL, '') AS StoreContWriteOffGL, ISNULL(INVSTORESCOSDIV.IsYardStore, 0) AS IsYardStore,       isnull(INVSTORESCOSDIV.StoreStatus, 0) as STORESTATUS      FROM INVSTORES INNER JOIN           (SELECT DISTINCT StkStore, BorgID             FROM INVENTORY) AS temp ON INVSTORES.StoreCode = temp.StkStore LEFT OUTER JOIN       INVSTORESCOSDIV ON temp.StkStore = INVSTORESCOSDIV.STORECODE AND temp.BorgID = INVSTORESCOSDIV.BORGID 