  CREATE VIEW LEDGERCODESALLOWED  AS  	SELECT LEDGERCODES.*, LEDGERCODESINGROUP.SYS, LEDGERCODESINGROUP.CONTRID, LEDGERCODESINGROUP.DIVID, LEDGERCODESINGROUP.PEID, LEDGERCODESINGROUP.BORGID  	FROM LEDGERCODES  	INNER JOIN LEDGERCODESINGROUP  	ON LEDGERCODES.LEDGERID = LEDGERCODESINGROUP.LEDGERID 