CREATE VIEW SUBCRECONS_ATTRIBUTES AS SELECT SUBCRECONS.* , A29.VALUE [Attribute.EC], A30.VALUE [Attribute.EI] FROM SUBCRECONS  LEFT OUTER JOIN ATTRIBVALUE A29 on A29.TABLENAME = 'SUBCRECONS' and A29.ATTRIBUTE = 'EC' and A29.COLKEY = SUBCRECONS.ReconID and A29.ARRAYINDEX = 0 LEFT OUTER JOIN ATTRIBVALUE A30 on A30.TABLENAME = 'SUBCRECONS' and A30.ATTRIBUTE = 'EI' and A30.COLKEY = SUBCRECONS.ReconID and A30.ARRAYINDEX = 0