  CREATE VIEW USERSININVSTORES  AS  SELECT DISTINCT TABLEINCLUDE.STORECODE, TABLEINCLUDE.USERID  FROM  (  	select INVSTORES.STORECODE, USERS.USERID  	from INVSTORES  	CROSS JOIN USERS  	left outer join USERINVSTORES on USERINVSTORES.USERID = USERS.USERID AND USERINVSTORES.STORECODE = INVSTORES.STORECODE  	WHERE INVSTORES.STORESTATUS <> 2  	AND ((USERINVSTORES.INC = 1) OR (USERINVSTORES.USERID is null))  ) as TABLEINCLUDE 