CREATE VIEW UifDeductable AS  SELECT TOP 100 PERCENT SUM(EMPED.THEMONEY) * 2 AS uifdeduc, EMPLOYEES.EMPNUMBER, EMPLOYEES.PAYROLLID, EMPED.PERIODNO  FROM EMPED LEFT OUTER JOIN  EDSETS ON EMPED.EDSHID = EDSETS.EDSHID AND EMPED.PRLID = EDSETS.PAYROLLID AND  EMPED.EDSNUMBER = EDSETS.EDSNUMBER AND EMPED.EDSCODE = EDSETS.EDSCODE RIGHT OUTER JOIN  EMPLOYEES ON EMPED.PRLID = EMPLOYEES.PAYROLLID AND EMPED.EMPNUMBER = EMPLOYEES.EMPNUMBER AND   EMPED.EDSHID = EMPLOYEES.EDSHID  GROUP BY EMPLOYEES.EMPNUMBER, EMPLOYEES.PAYROLLID, EDSETS.EDSUSEAS, EMPED.PERIODNO  HAVING (EDSETS.EDSUSEAS = N'UIF')  ORDER BY EMPLOYEES.EMPNUMBER 