/****** Object:  View [dbo].[bs_EMPLOYEE_CC_PER_YEAR_PERIOD]    Committed by VersionSQL https://www.versionsql.com ******/

CREATE VIEW bs_EMPLOYEE_CC_PER_YEAR_PERIOD AS SELECT TOP 100 PERCENT PAYROLLID, EMPNUMBER, SUM(NT) AS NT, SUM(OT1) AS OT1, SUM(OT2) AS OT2, SUM(OT3) AS OT3, SUM(OT4) AS OT4, SUM(OT5) AS OT5, PERIODNO, YEARNO FROM CLOCKCARDS WHERE (RECTYPE = N'PRESENT') GROUP BY PAYROLLID, EMPNUMBER, PERIODNO, YEARNO, RECTYPE ORDER BY YEARNO, PERIODNO, EMPNUMBER 