CREATE VIEW dbo.bs_PERIODEND_CCHOURS AS SELECT TOP 100 PERCENT PAYROLLID, YEARNO, PERIODNO, RUNNO, EMPNUMBER, SUM(NT) AS NT, SUM(OT1) AS OT1, SUM(OT2) AS OT2, SUM(OT3) AS OT3, SUM(OT4) AS OT4, SUM(OT5) AS OT5, PENUMBER, ALLOCATION, CONTRNUMBER, LEDGERCODE, DIVID FROM dbo.CLOCKCARDS GROUP BY PAYROLLID, YEARNO, PERIODNO, RUNNO, EMPNUMBER, PENUMBER, ALLOCATION, CONTRNUMBER, LEDGERCODE, DIVID ORDER BY PAYROLLID, YEARNO, PERIODNO, RUNNO, EMPNUMBER 