CREATE VIEW dbo.bs_PERIODEND_HOURS AS SELECT TOP 100 PERCENT PAYROLLID, SUM(NT) AS NT, PERIODNO, RUNNO, YEARNO, SUM(OT1) AS OT1, SUM(OT2) AS OT2, SUM(OT3) AS OT3, SUM(OT4) AS OT4, SUM(OT5) AS OT5 FROM dbo.CLOCKCARDS GROUP BY PAYROLLID, PERIODNO, RUNNO, YEARNO ORDER BY PAYROLLID, YEARNO, PERIODNO, RUNNO 