CREATE VIEW dbo.empccid AS SELECT     TOP 100 PERCENT MAX(CCID) AS CCID, PAYROLLID, EMPNUMBER , SUM(NT) AS SumNT, SUM(OT1) AS SumOT1, SUM(OT2) AS SumOT2, SUM(OT3) AS SumOT3, SUM(OT4) AS SumOT4, SUM(OT5) AS SumOT5, SUM(SHIFTSTW) AS Sumshifts , YEARNO, PERIODNO, RUNNO FROM         dbo.CLOCKCARDS GROUP BY PAYROLLID, EMPNUMBER, YEARNO, PERIODNO, RUNNO ORDER BY EMPNUMBER, PAYROLLID, YEARNO, PERIODNO, RUNNO 